import React from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

const Book = ({ book }) => {
  return (
    <div className="book">
      <a href="">
        <figure className="book__img--wrapper">
          <img src={book.url} alt="" className="book__img" />
        </figure>
      </a>
      <div className="book__title">
        <a href="/" className="book__title--link">
          {book.title}
        </a>
      </div>
      <div className="book__ratings">
        {
            new Array(4).fill(0).map((element, index) => <FontAwesomeIcon icon="star" />)
        }
      </div>
      <div className="book__price">
        {book.salePrice ? (
          <>
            <span className="book__price--normal">CHF{book.originalPrice.toFixed(2)}</span>
            CHF{book.salePrice.toFixed(2)}
          </>
        ) : (
          <>CHF {book.originalPrice.toFixed(2)}</>
        )}
        
      </div>
    </div>
  );
};

export default Book;
